package com.example.trinhle.languagejson.adapter;import android.content.Context;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import android.widget.Toast;import com.example.trinhle.languagejson.Category;import com.example.trinhle.languagejson.MainActivity;import com.example.trinhle.languagejson.R;import java.util.List;/** * Created by Trinh Le on 28/07/2016. */public class RecyclerViewAdapter extends RecyclerView.Adapter<RecyclerViewAdapter.RecyclerViewHolder> {    private List<Category> categories;    private Context context;    public RecyclerViewAdapter(Context context, List<Category> categories) {        this.context = context;        this.categories = categories;    }    @Override    public RecyclerViewHolder onCreateViewHolder(ViewGroup parent, int viewType) {        View layoutView = LayoutInflater.from(parent.getContext()).inflate(R.layout.category_item, null);        RecyclerViewHolder viewHolder = new RecyclerViewHolder(layoutView);        return viewHolder;    }    @Override    public void onBindViewHolder(RecyclerViewHolder holder, final int position) {        holder.tvCategoryName.setText(categories.get(position).getCategoryName());        holder.tvDescription.setText(categories.get(position).getDescription());        holder.tvNumCol.setText(categories.get(position).getNumCol());    }    @Override    public int getItemCount() {        return categories.size();    }    public static class RecyclerViewHolder extends RecyclerView.ViewHolder {        public TextView tvCategoryName;        public TextView tvDescription;        public TextView tvNumCol;        public RecyclerViewHolder(final View itemView) {            super(itemView);            tvCategoryName = (TextView) itemView.findViewById(R.id.tv_category_name);            tvDescription = (TextView) itemView.findViewById(R.id.tv_description);            tvNumCol = (TextView) itemView.findViewById(R.id.tv_num_col);            itemView.setOnClickListener(new View.OnClickListener() {                @Override                public void onClick(View view) {                    Toast.makeText(view.getContext(), " " + getAdapterPosition(), Toast.LENGTH_SHORT).show();                }            });        }    }}